package com.invoice.bean.db;public class SheetPayment  {	/** 门店 **/	private String shopid;	/** 单号 **/	private String sheetid;	/** 行号 **/	private Integer rowno;	/** 支付方式编码 **/	private String payid;	private String payname;	/** 支付金额 **/	private Double amt;		/** 是否开票**/	private String isinvoice;		public String getShopid() {		return shopid;	}	public void setShopid(String shopid) {		this.shopid = shopid;	}	public String getSheetid() {		return sheetid;	}	public void setSheetid(String sheetid) {		this.sheetid = sheetid;	}	public Integer getRowno() {		return rowno;	}	public void setRowno(Integer rowno) {		this.rowno = rowno;	}	public String getPayid() {		return payid;	}	public void setPayid(String payid) {		this.payid = payid;	}	public Double getAmt() {		return amt;	}	public void setAmt(Double amt) {		this.amt = amt;	}	public String getIsinvoice() {		return isinvoice;	}	public void setIsinvoice(String isinvoice) {		this.isinvoice = isinvoice;	}	public String getPayname() {		return payname;	}	public void setPayname(String payname) {		this.payname = payname;	}}