SELECT a.sheetid,"" as xfzEmail,a.totalAmount,"" as fphm, a.tradeDate as fprq, "无接收邮箱" as iqmsg,a.gmftax,a.gmfname,a.gmfbank,d.SHOPNAME from invoice_sale_head a,shop d  
