SELECT count(*) from invoice_head a
join invque b on a.iqseqno=b.iqseqno
where b.iqentid=#{entid} 

   and b.invoiceType=#{invoicetype} 


   and b.status=#{status} 


   and b.iqperson=#{loginid} 


    and a.fpDM=#{fpdm}


   and a.fphm=#{fphm}


   and b.iqshop=#{shopid}

SELECT a.*,b.iqperson from invoice_head a
join invque b on a.iqseqno=b.iqseqno
where b.iqentid=#{entid} 

   and b.invoiceType=#{invoicetype} 


   and b.status=#{status} 


   and b.iqperson=#{loginid} 


    and a.fpDM=#{fpdm}


   and a.fphm=#{fphm}


   and b.iqshop=#{shopid}


   EXISTS (SELECT 1 from invque_list c where c.iqseqno=b.iqseqno
   and c.sheetid=#{sheetid}
   and c.sheettype=#{sheettype}
   )

order by a.fphm desc

	limit ${pagestart},${pagesize}

SELECT * from invoice_detail where invoiceid=#{invoiceid}
order by seqno asc

	limit ${pagestart},${pagesize}

    SELECT b.*, b.YHZCBS as taxpre,b.LSLBS as zerotax,b.ZZSTSGL as taxprecon from invoice_head a,invoice_detail b
	where a.invoiceid = b.invoiceid 
	and a.fpDM=#{yfpdm}
	and a.fpHM=#{yfphm}
	ORDER BY b.seqno asc

SELECT d.sheetid,a.fphm,b.goodsid,b.goodsName,b.price,b.amt,b.qty,a.fpRQ,c.iqperson,b.taxRate,b.taxitemid,e.taxitemname
from invoice_head a,invoice_detail b,invque c,invque_list d,taxitem e where a.iqseqno=c.iqseqno and a.invoiceid = b.invoiceid 
 and b.taxitemid = e.taxitemid and c.iqseqno=d.iqseqno and a.invoiceType =0 and a.status=100 
and c.iqentid=#{entid} 

	and c.iqsource=#{iqsource} 

and d.shopid=#{shopid} 

	and a.fphm = #{fphm} 


	and b.taxitemid = #{taxitemid} 


	and b.goodsid = #{goodsid} 


	and d.sheetid=#{sheetid} 


	and a.fpRQ >= #{startdate} 


	and a.fpRQ <= #{enddate} 


	and b.goodsName like CONCAT('%',#{goodsname},'%') 

order by a.fphm desc

	limit ${pagestart},${pagesize}

SELECT count(*)
from invoice_head a,invoice_detail b,invque c,invque_list d,taxitem e where a.iqseqno=c.iqseqno and a.invoiceid = b.invoiceid 
 and b.taxitemid = e.taxitemid and c.iqseqno=d.iqseqno and a.invoiceType =0 and a.status=100 
and c.iqentid=#{entid} 

	and c.iqsource=#{iqsource} 

and d.shopid=#{shopid}

	and a.fphm = #{fphm} 


	and b.taxitemid = #{taxitemid} 


	and b.goodsid = #{goodsid} 


	and d.sheetid=#{sheetid} 


	and a.fpRQ >= #{startdate}


	and a.fpRQ <= #{enddate}


	and b.goodsName like CONCAT('%',#{goodsname},'%')

SELECT a.fphm,b.goodsid,b.goodsName,b.price,SUM(b.amt) as zamt,SUM(b.qty) as zqty,a.fpRQ,c.iqperson
from invoice_head a,invoice_detail b,invque c,invque_list d,taxitem e where a.iqseqno=c.iqseqno and a.invoiceid = b.invoiceid 
 and b.taxitemid = e.taxitemid and c.iqseqno=d.iqseqno and a.invoiceType =0 and a.status=100 
and c.iqentid=#{entid} 

	and c.iqsource=#{iqsource} 

and d.shopid=#{shopid} 

	and a.fphm = #{fphm} 


	and b.taxitemid = #{taxitemid} 


	and b.goodsid = #{goodsid} 


	and d.sheetid=#{sheetid} 


	and a.fpRQ >= #{startdate} 


	and a.fpRQ <= #{enddate} 


	and b.goodsName like CONCAT('%',#{goodsname},'%') 

order by a.fphm desc

select count(*) from invque a,invque_list b,invoice_head c where a.iqseqno=b.iqseqno and a.iqseqno=c.iqseqno
and a.iqentid=#{entid} 
and a.iqsource='1' 

	and b.shopid=#{shopid} 


	and b.sheetid=#{sheetid} 


	and a.rtfphm=#{fphm} 


	and a.rtkprq >= #{startdate} 


	and a.rtkprq <= #{enddate} 

order by a.rtkprq desc

select b.sheetid,a.*,c.* from invque a,invque_list b,invoice_head c where a.iqseqno=b.iqseqno and a.iqseqno=c.iqseqno
and a.iqentid=#{entid} 
and a.iqsource='1' 

	and b.shopid=#{shopid} 


	and b.sheetid=#{sheetid} 


	and a.rtfphm=#{fphm} 


	and a.rtkprq >= #{startdate} 


	and a.rtkprq <= #{enddate} 

order by a.rtkprq desc

	limit ${pagestart},${pagesize}

select SUM(totalamt) as zamt from invque a,invque_list b,invoice_head c where a.iqseqno=b.iqseqno and a.iqseqno=c.iqseqno
and a.iqentid=#{entid} 
and a.iqsource='1' 

	and b.shopid=#{shopid} 


	and b.sheetid=#{sheetid} 


	and a.rtfphm=#{fphm} 


	and a.rtkprq >= #{startdate} 


	and a.rtkprq <= #{enddate} 

order by a.rtkprq desc

mapper.update:
update invoice_head 
set status=#{status},fpDM=#{fpDM},fpHM=#{fpHM},fpskm=#{fpskm},fpjym=#{fpjym},fpewm=#{fpewm},pdf=#{pdf}
where iqseqno=#{iqseqno} and status != 100
